import React from 'react';
import './BooksList.css';

class BooksList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {booksList: []};
    }

componentDidMount() {
    fetch('http://localhost:8080/book')
    .then(response => response.json())
    .then(jsonResponse=> {
        this.setState({booksList: jsonResponse});
    })
}

renderList = () => {
  return  this.state.booksList.map((book, index) => {
        return <tr key = {index}>

            <td>{book.booksCategory}</td>
            <td>{book.title}</td>
            <td>{book.description}</td>
            <td>{book.author}</td>
            <td>{book.prize}</td>
        </tr>

    })
}

    render() {
        return(
            <div id = "all"> 
            <h1>Books list!</h1>
            <div id = "books">
            <table>
                <tr>
                     <th>Kategoria</th><th>Tytu≈Ç</th><th>Opis</th><th>Autor</th><th>Cena</th>
                        <tr> {this.renderList()}</tr>
                </tr>
            </table>
            </div>
            </div>
        )
    }
}
export default BooksList;
