import React from 'react';

class BooksList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {booksList: []};
    }

componentDidMount() {
    fetch('localhost:8080/book')
    .then(response => response.json())
    .then(jsonResponse=> {
        this.setState({booksList: jsonResponse});
    })
}

renderList = () => {
  return  this.state.booksList.map((book, index) => {
        return <li key = {index}>
            <p>Kategoria: {book.booksCategory}</p>
            <p>Tytu≈Ç: {book.title}</p>
            <p>Opis: {book.description}</p>
            <p>Autor: {book.author}</p>
            <p>Cena: {book.prize}</p>
            <iframe width="560" height="315" src= {book.url} title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>

        </li>
    })
}

    render() {
        return(
            <div>
                <button> </button>
            <h1>Books list!</h1>
            <ul>
                {this.renderList()}
            </ul>
            </div>
        )
    }
}
export default BooksList;
