import React from 'react';
import './style/reset.css';
import './MainPageTest.css';

class MainPageTest extends React.Component {
    constructor(props) {
        super(props);
        this.state = {itemList: [],category: [] } 

    }
     url = 'https://favael-webshop.herokuapp.com/book/' + this.props.category

componentDidMount() {
    Promise.all([fetch(this.url)])
    // Promise.all([fetch('http://localhost:8080/book/szachy'), fetch('http://localhost:8080/book/dramat'), fetch('http://localhost:8080/book/gotowanie'), fetch('http://localhost:8080/book/scfi'), fetch('http://localhost:8080/book/geografia'), fetch('http://localhost:8080/book/romans'), fetch('http://localhost:8080/book/historia')])

      .then(([res1]) => { 
         return Promise.all([res1.json()]) 
      })
      .then(([res1]) => {
        this.setState({chessBooks: res1})
      });
}


renderItemList = () => {
    return  this.state.itemList.map((book) => {
          return <button class = "buttonBook" >
          <img src={book.url} alt={book.title} width = "110" height = "150"></img>
          </button>
      })
  }

chooseChess = () => {
    this.category = "szachy"}

chooseDramat = () => {
    this.setState({chosenItem: this.dramatBooks})
}
chooseCook= () => {
    this.setState({chosenItem: this.cooksBooks})
}
chooseSCFI = () => {
    this.setState({chosenItem: this.scfiBooks})
}
chooseGEO = () => {
    this.setState({chosenItem: this.geographyBooks})
}
chooseRomans = () => {
    this.setState({chosenItem: this.romansBooks})
}
chooseHistory = () => {
    this.setState({chosenItem: this.historyBooks})
}



  renderDramatList = () => {
    return  this.state.dramatBooks.map((book) => {
          return <button class = "dropdown-content">
          <img src={book.url} alt={book.title} width = "110" height = "150"></img>
          </button>
      })
  }
  renderCookList = () => {
    return  this.state.cooksBooks.map((book) => {
          return <button class = "buttonBook">
          <img src={book.url} alt={book.title} width = "110" height = "150"></img>
          </button>
      })
  }

  renderScfiBooksList = () => {
    return  this.state.scfiBooks.map((book) => {
          return <button class = "buttonBook">
          <img src={book.url} alt={book.title} width = "110" height = "150"></img>
          </button>
      })
  }

  renderGeographyBooksList = () => {
    return  this.state.geographyBooks.map((book) => {
          return <button class = "buttonBook">
          <img src={book.url} alt={book.title} width = "110" height = "150"></img>
          </button>
      })
  }

  renderRomansBooksList = () => {
    return  this.state.romansBooks.map((book) => {
          return <div class = "buttonBook">
          <img src={book.url} alt={book.title} width = "110" height = "150"></img>
          </div>
      })
  }

  renderHistoryBooksList = () => {
    return  this.state.historyBooks.map((book) => {
          return <button class = "buttonBook">
          <img src={book.url} alt={book.title} width = "110" height = "150"></img>
          </button>

      })
  }
  renderChoosenitemsList = () => {
    return  this.state.chosenItem.map((book) => {
          return <button class = "buttonBook">
          <img src={book.url} alt={book.title} width = "110" height = "150"></img>
          </button>

      })
  }

    render() {
        console.log(this.itemList())  
        return(

            <div id = "page"> 

                <div class = "navbar">
                    <div class = "dropbtn">
                    <button class="dropbtn">Moje konto
                    </button>
                    <button class="dropbtn">Zaloguj
                    </button>
                    <button class="dropbtn">Mój koszyk
                    </button>

                    </div>
                    <div class = "header-text">
                    "KUPUJMY KSIĄŻKI!"

                    </div>

                </div>

                <div class="navbar">
                    <a href="#home">Home</a>
                    <a href="#news">News</a>

                            <div class="dropdown">
                                <button class="dropbtn" onClick = {this.chooseChess}>Szachy 
                                </button>
                                    <div class="dropdown-content">
                                            {this.renderItemList()}   

                                    </div>
                            </div>

                            <div class="dropdown">
                                <button class="dropbtn" onClick = {this.chooseDramat}>Dramat
                                </button>
                                    <div class="dropdown-content">

                                    </div>
                            </div>

                            <div class="dropdown">
                                <button class="dropbtn" onClick = {this.chooseHistory}>Historia
                                </button>
                                    <div class="dropdown-content">

                                    </div>
                            </div>

                            <div class="dropdown">
                                <button class="dropbtn" onClick = {this.chooseCook}>Gotowanie
                                </button>
                                    <div class="dropdown-content">

                                    </div>
                            </div>

                            <div class="dropdown">
                                <button class="dropbtn"onClick = {this.chooseRomans}>Romans
                                </button>
                                    <div class="dropdown-content" >

                                    </div>
                            </div>

                            <div class="dropdown">
                                <button class="dropbtn" onClick = {this.chooseSCFI}>Sci-Fi 
                                </button>
                                    <div class="dropdown-content">

                                    </div>
                            </div>

                            <div class="dropdown">
                                <button class="dropbtn">Dramat
                                </button>
                                    <div class="dropdown-content">

                                    </div>
                            </div>
                            </div>
                            </div>

        )}

}

export default MainPageTest;
